    function showPopup(popupID, repeatNo, that, playerNotifications) {
      var notificationsNo = playerNotifications.length;
      var popupContainer = $gb(popupID),
        popupWindow = popupContainer.find('.gb_popup_content'),
        staggeringElements = popupContainer.find('.p_stagger'),
        closeButton = popupContainer.find('.close_popup')
        ;

      var openTween = new TimelineMax({
        repeat: repeatNo, repeatDelay: 4, onRepeat: function () {
          //openTweenS.kill();
          notificationsNo -= 1;
          data.notification = playerNotifications[notificationsNo];
          that.ractive.set("data", data);

        }
      });
      openTween
        .set(popupContainer, { className: "+=opened" })
        .from(popupWindow, 1.2, { scale: .5, opacity: 0, ease: Elastic.easeOut.config(1, 0.7) }, '-=.3')
        .staggerFrom(staggeringElements, .6, { y: '+=30', opacity: 0, ease: Power4.easeOut }, .1, '-=.7')
        ;

      closeButton.on('click', function () {
        var closeTween = new TimelineMax();

        closeTween
          .to(popupWindow, .4, { scale: 1.1, opacity: 0, ease: Power3.easeOut })
          .to(popupContainer, .4, { autoAlpha: 0 }, '-=.4')
          .set(popupContainer, { className: "-=opened" })
          .set([popupContainer, popupWindow, staggeringElements], { clearProps: "all" })
          ;

      });

      return openTween;
    }

